CREATE DEFINER=`root`@`localhost` PROCEDURE `rent_rentable`(in cID int(11),in rID int(11),out done int(11))
BEGIN
DECLARE amount int(11);
DECLARE cm int(11);
select wallet into cm from customer where ID=cID;
