IF cm<amount then
SET done = 0;
else
SET done = 1;
insert into r_customer_rentable(customer_id,rentable_id,days_left,active) values(cID,rID,5,1);
update customer SET wallet=cm-amount where ID=cID;
END IF;
END ;;
